import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

// Create Auth Context
export const AuthContext = createContext();

// Custom hook to use auth
export const useAuth = () => useContext(AuthContext);

// Auth Provider
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    // Load user from localStorage if available
    const savedUser = localStorage.getItem("user");
    return savedUser ? JSON.parse(savedUser) : null;
  });

  const [token, setToken] = useState(() => localStorage.getItem("token") || "");

  // Save to localStorage whenever user or token changes
  useEffect(() => {
    if (user) localStorage.setItem("user", JSON.stringify(user));
    else localStorage.removeItem("user");

    if (token) localStorage.setItem("token", token);
    else localStorage.removeItem("token");
  }, [user, token]);

  // Register user
  const registerUser = async ({ username, email, password }) => {
    try {
      const res = await axios.post("http://localhost:5000/api/auth/register", {
        username,
        email,
        password,
      });
      setUser(res.data.user);
      setToken(res.data.token);
    } catch (err) {
      console.error("Register error:", err.response?.data?.message || err.message);
      alert(err.response?.data?.message || "Registration failed");
    }
  };

  // Login user
  const login = async ({ username, password }) => {
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });
      setUser(res.data.user);
      setToken(res.data.token);
    } catch (err) {
      console.error("Login error:", err.response?.data?.message || err.message);
      alert(err.response?.data?.message || "Invalid username or password");
    }
  };

  // Logout user
  const logout = () => {
    setUser(null);
    setToken("");
  };

  return (
    <AuthContext.Provider value={{ user, token, registerUser, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
