import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./Auth.css";
import logo from "../assets/logo.png";
const Login = () => {
  const { loginUser } = useAuth();
  const [formData, setFormData] = useState({ email: "", password: "" });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleSubmit = (e) => { e.preventDefault(); loginUser(formData); };

  return (
    <div className="auth-page">
      <div className="auth-header">
        <img src="/logo192.png" alt="Logo" className="auth-logo"/>
        <h1 className="auth-appname">Connectly</h1>
        <p className="auth-slogan">Share your world üåç</p>
      </div>
      <div className="auth-container">
        <h2>Login</h2>
        <p className="auth-tagline">Welcome back</p>
        <form onSubmit={handleSubmit}>
          <input type="email" name="email" placeholder="Email" onChange={handleChange} required/>
          <input type="password" name="password" placeholder="Password" onChange={handleChange} required/>
          <button type="submit">Login</button>
        </form>
        <p className="auth-link">Don't have an account? <a href="/register">Register</a></p>
      </div>
    </div>
  );
};

export default Login;
