import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import PostList from "./PostList";
import Avatar from "./Avatar"; // Avatar upload component
import "./Auth.css";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) return;
    // Fetch posts only by the logged-in user
    axios
      .get(`http://localhost:5000/api/posts/user/${user.id}`)
      .then((res) => setPosts(res.data))
      .catch((err) => console.error(err));
  }, [user]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const handleAvatarClick = () => {
    navigate("/profile");
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-topbar">
        <span className="dashboard-username">Hello, {user.username}</span>
        <div className="dashboard-avatar-section" onClick={handleAvatarClick}>
          <img
            src={`http://localhost:5000/uploads/${user.avatar}`}
            alt="Avatar"
            className="dashboard-avatar"
          />
        </div>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <PostList posts={posts} />
    </div>
  );
};

export default Dashboard;
