import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Navbar from "./Navbar";
import "./Auth.css";

const Profile = () => {
  const { id: paramId } = useParams();
  const { user } = useAuth();
  const userId = paramId || user?._id;

  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch profile
  useEffect(() => {
    const fetchProfile = async () => {
      if (!userId) return;
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setProfile(res.data);
        setPosts(res.data.posts || []);
      } catch (err) {
        console.error("Error fetching profile:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [userId]);

  // Avatar change handler
  const handleAvatarChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append("avatar", file);

    try {
      await axios.put(`http://localhost:5000/api/users/${userId}/avatar`, formData);
      // Reload avatar without refreshing the page
      setProfile({ ...profile, avatar: URL.createObjectURL(file) });
    } catch (err) {
      console.error("Error updating avatar:", err);
    }
  };

  if (!userId) return <p>Loading profile...</p>;

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-main">
        {loading ? (
          <div className="spinner-container">
            <div className="spinner"></div>
          </div>
        ) : profile ? (
          <>
            {/* Profile Header */}
            <div className="profile-header" style={{ flexDirection: "row", justifyContent: "flex-start", alignItems: "center", gap: "20px" }}>
              <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
                <img
                  src={profile.avatar || "/default-avatar.png"}
                  alt="avatar"
                  className="profile-avatar-big"
                />
                {user && user._id === userId && (
                  <label className="avatar-upload-label">
                    Change Avatar
                    <input type="file" onChange={handleAvatarChange} />
                  </label>
                )}
              </div>
              <div>
                <h2>{profile.username}</h2>
                <div style={{ display: "flex", gap: "15px", marginTop: "8px", fontSize: "14px", color: "#555" }}>
                  <span><strong>{posts.length}</strong> posts</span>
                  <span><strong>{profile.followers?.length || 0}</strong> followers</span>
                  <span><strong>{profile.following?.length || 0}</strong> following</span>
                </div>
              </div>
            </div>

            {/* Posts */}
            <div className="post-feed" style={{ marginTop: "30px" }}>
              {posts.length > 0 ? (
                posts.map((post) => (
                  <div key={post._id} className="post-card">
                    {post.image && <img src={post.image} alt="Post" className="post-image" />}
                    <p>{post.caption}</p>
                  </div>
                ))
              ) : (
                <p>No posts yet.</p>
              )}
            </div>
          </>
        ) : (
          <p>Profile not found.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
