import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import "./Auth.css";

const Avatar = () => {
  const { user, login } = useAuth();
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return alert("Please select a file first");
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append("avatar", selectedFile);

      const res = await axios.post(
        `http://localhost:5000/api/auth/upload-avatar/${user.id}`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      // Update user context with new avatar
      login({ ...user, avatar: res.data.avatar });
      setSelectedFile(null);
      setPreview(null);
    } catch (err) {
      console.error(err);
      alert("Failed to update avatar");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="avatar-container">
      <div className="avatar-preview">
        <img
          src={preview || `http://localhost:5000/uploads/${user.avatar}`}
          alt="Avatar"
          className="avatar-img"
        />
      </div>

      <input
        type="file"
        accept="image/*"
        id="avatar-input"
        style={{ display: "none" }}
        onChange={handleFileChange}
      />

      <div className="avatar-buttons">
        <label htmlFor="avatar-input" className="select-btn">
          {preview ? "Change" : "Upload"}
        </label>
        {preview && (
          <button onClick={handleUpload} disabled={loading} className="upload-btn">
            {loading ? "Uploading..." : "Save"}
          </button>
        )}
      </div>
    </div>
  );
};

export default Avatar;
