import React, { createContext, useContext, useEffect, useState } from "react";
import axios from "axios";

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // { _id, username, email, avatar, ... }
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // load stored user & token
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");
    if (storedUser && token) {
      setUser(JSON.parse(storedUser));
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    }
    setLoading(false);
  }, []);

  const registerUser = async (formData) => {
    // formData = { username, email, password }
    const res = await axios.post("http://localhost:5000/api/auth/register", formData);
    // expected { user, token }
    setUser(res.data.user);
    localStorage.setItem("user", JSON.stringify(res.data.user));
    localStorage.setItem("token", res.data.token);
    axios.defaults.headers.common["Authorization"] = `Bearer ${res.data.token}`;
    return res.data;
  };

  const loginUser = async (credentials) => {
    // credentials = { username, password }
    const res = await axios.post("http://localhost:5000/api/auth/login", credentials);
    // expected { user, token }
    setUser(res.data.user);
    localStorage.setItem("user", JSON.stringify(res.data.user));
    localStorage.setItem("token", res.data.token);
    axios.defaults.headers.common["Authorization"] = `Bearer ${res.data.token}`;
    return res.data;
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    delete axios.defaults.headers.common["Authorization"];
  };

  const refreshUser = (newUser) => {
    // update user in context/localStorage (used after avatar change etc.)
    setUser(newUser);
    localStorage.setItem("user", JSON.stringify(newUser));
  };

  return (
    <AuthContext.Provider value={{ user, loading, registerUser, loginUser, logout, refreshUser }}>
      {children}
    </AuthContext.Provider>
  );
};
