import React, { createContext, useContext, useState } from "react";

// ✅ Create context
const AuthContext = createContext();

// ✅ Provider
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // Mock register function (replace with your backend API call)
  const registerUser = (data) => {
    console.log("Register data:", data);
    // Example: set user locally
    setUser({ username: data.username, email: data.email });
  };

  // Mock login function (replace with your backend API call)
  const loginUser = (data) => {
    console.log("Login data:", data);
    // Example: set user locally
    setUser({ email: data.email });
  };

  // Mock logout function
  const logoutUser = () => {
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, registerUser, loginUser, logoutUser }}>
      {children}
    </AuthContext.Provider>
  );
};

// ✅ Custom hook to use the context
export const useAuth = () => useContext(AuthContext);
