import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Navbar from "./Navbar";
import "./Auth.css";

const Profile = () => {
  const { id: paramId } = useParams();
  const { user } = useAuth();
  const userId = paramId || user?._id;

  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchProfile = async () => {
      if (!userId) return;
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setProfile(res.data);
        setPosts(res.data.posts || []);
      } catch (err) {
        console.error("Error fetching profile:", err);
      }
    };
    fetchProfile();
  }, [userId]);

  const handleAvatarChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("avatar", file);

    try {
      await axios.put(`http://localhost:5000/api/users/${userId}/avatar`, formData);
      // Refresh profile after avatar update
      const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
      setProfile(res.data);
    } catch (err) {
      console.error("Error updating avatar:", err);
    }
  };

  if (!profile) {
    return (
      <div className="dashboard-layout">
        <Navbar />
        <div className="dashboard-main">
          <p>Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-main">
        <div className="profile-header">
          <div className="avatar-upload-container">
            <img
              src={profile.avatar || "/default-avatar.png"}
              alt="avatar"
              className="profile-avatar-big"
            />
            {user && user._id === userId && (
              <label className="upload-avatar-label">
                Change Avatar
                <input type="file" onChange={handleAvatarChange} />
              </label>
            )}
          </div>

          <div className="profile-info">
            <h2 className="profile-username">{profile.username}</h2>
            <div className="profile-stats">
              <span>{posts.length} posts</span>
              <span>{profile.followers?.length || 0} followers</span>
              <span>{profile.following?.length || 0} following</span>
            </div>
          </div>
        </div>

        <div className="user-posts">
          {posts.length > 0 ? (
            posts.map((post) => (
              <div key={post._id} className="user-post-card">
                {post.image && <img src={post.image} alt="Post" />}
                {post.caption && <p>{post.caption}</p>}
              </div>
            ))
          ) : (
            <p>No posts yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
