import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import PostList from "./PostList";
import Avatar from "./Avatar";
import { useNavigate } from "react-router-dom";
import "./Auth.css";

const Profile = () => {
  const { user, logout } = useAuth();
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) return; // Stop if no user
    const fetchPosts = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/posts/user/${user.id}`);
        setPosts(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPosts();
  }, [user]); // only runs once after user is loaded

  if (!user) return <div>Loading...</div>; // prevent blank page

  const handleDeleteProfile = async () => {
    if (!window.confirm("Are you sure you want to delete your profile?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${user.id}`);
      logout();
      navigate("/login");
    } catch (err) {
      console.error(err);
      alert("Failed to delete profile");
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img
          src={`http://localhost:5000/uploads/${user.avatar}`}
          alt="Avatar"
          className="profile-avatar-big"
        />
        <h2>{user.username}</h2>
        <div className="profile-buttons">
          <Avatar /> {/* Change avatar */}
          <button onClick={handleDeleteProfile} className="delete-btn">
            Delete Profile
          </button>
        </div>
      </div>
      <PostList posts={posts} /> {/* Only the user's posts */}
    </div>
  );
};

export default Profile;
