import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const Profile = () => {
  const { id } = useParams(); // id will be 'me' sometimes
  const { user, refreshUser } = useAuth();
  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [avatarFile, setAvatarFile] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const targetId = id === "me" ? user?._id : id;

  useEffect(() => {
    if (!targetId) {
      navigate("/login");
      return;
    }

    let mounted = true;
    const fetchProfile = async () => {
      try {
        const [pRes, postsRes] = await Promise.all([
          axios.get(`http://localhost:5000/api/users/${targetId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
          }),
          axios.get(`http://localhost:5000/api/posts?user=${targetId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
          }),
        ]);
        if (mounted) {
          setProfile(pRes.data);
          setPosts(postsRes.data || []);
        }
      } catch (err) {
        console.error(err);
      } finally {
        if (mounted) setLoading(false);
      }
    };
    fetchProfile();
    return () => (mounted = false);
  }, [targetId, navigate]);

  const uploadAvatar = async (e) => {
    e.preventDefault();
    if (!avatarFile) return;
    const fd = new FormData();
    fd.append("avatar", avatarFile);
    try {
      const res = await axios.post(`http://localhost:5000/api/users/${user._id}/avatar`, fd, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
          "Content-Type": "multipart/form-data",
        },
      });
      // server should return updated user object
      refreshUser(res.data.user);
      setProfile(res.data.user);
      setAvatarFile(null);
      alert("Avatar updated");
    } catch (err) {
      console.error(err);
      alert("Failed to upload avatar");
    }
  };

  if (loading) return <p style={{ textAlign: "center" }}>Loading profile...</p>;
  if (!profile) return <p style={{ textAlign: "center" }}>Profile not found.</p>;

  return (
    <div className="center-column">
      <div className="auth-box">
        <h3>{profile.username}'s Profile</h3>
        <img src={profile.avatar || "/default-avatar.png"} alt="avatar" style={{ width: 120, height: 120, borderRadius: "50%" }} />
        {user && user._id === profile._id && (
          <form onSubmit={uploadAvatar} className="auth-form">
            <input type="file" accept="image/*" onChange={(e) => setAvatarFile(e.target.files[0])} />
            <button type="submit">Upload Avatar</button>
          </form>
        )}
      </div>

      <div className="auth-box" style={{ marginTop: 20, width: "100%" }}>
        <h4>Posts</h4>
        {posts.length === 0 ? <p>No posts.</p> : posts.map((p) => (
          <div key={p._id} style={{ marginBottom: 12 }}>
            <img src={p.image} alt="" style={{ maxWidth: "100%" }} />
            <p>{p.caption}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Profile;
