import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const AvatarUpload = () => {
  const { user, login } = useAuth(); // login function to update user in context
  const [file, setFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert("Select an image first");

    const formData = new FormData();
    formData.append("avatar", file);

    try {
      const res = await axios.put("http://localhost:5000/api/users/avatar", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${user.token}`, // send JWT
        },
      });

      // Update user in context
      login({ ...user, avatar: res.data.avatar });
      alert("Avatar updated!");
    } catch (err) {
      console.error(err);
      alert("Failed to update avatar");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="file" accept="image/*" onChange={(e) => setFile(e.target.files[0])} />
      <button type="submit">Upload Avatar</button>
    </form>
  );
};

export default AvatarUpload;
