import React from "react";
import "./Auth.css";

const PostCard = ({ post, userId, onDelete }) => {
  // Determine if current user owns the post
  const isOwner = userId === (post.user._id || post.user);

  return (
    <div className="post-card">
      <div className="post-header">
        <img
          className="post-avatar"
          src={
            post.user?.avatar
              ? post.user.avatar.startsWith("http")
                ? post.user.avatar
                : `/uploads/${post.user.avatar}`
              : "/default-avatar.png"
          }
          alt="avatar"
        />
        <div>
          <span className="post-username">{post.user?.username || "Unknown"}</span>
          <br />
          <span className="post-time">
            {new Date(post.createdAt).toLocaleString()}
          </span>
        </div>
      </div>

      <div className="post-content">
        <p className="post-caption">{post.caption || post.content}</p>
        {post.image && (
          <img
            className="post-image"
            src={post.image.startsWith("http") ? post.image : `/uploads/${post.image}`}
            alt="post"
          />
        )}
      </div>

      {isOwner && (
        <button className="delete-btn" onClick={() => onDelete(post._id)}>
          Delete
        </button>
      )}
    </div>
  );
};

export default PostCard;
