import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./Auth.css";
import logo from "../assets/logo.png";

const Register = () => {
  const { registerUser } = useAuth();
  const [formData, setFormData] = useState({ username: "", email: "", password: "" });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleSubmit = (e) => { e.preventDefault(); registerUser(formData); };

  return (
    <div className="auth-page">
      <div className="auth-header">
        <img src="/logo192.png" alt="Logo" className="auth-logo"/>
        <h1 className="auth-appname">Connectly</h1>
        <p className="auth-slogan">Share your world üåç</p>
      </div>
      <div className="auth-container">
        <h2>Create Account</h2>
        <p className="auth-tagline">Join our social family today</p>
        <form onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="Username" onChange={handleChange} required/>
          <input type="email" name="email" placeholder="Email" onChange={handleChange} required/>
          <input type="password" name="password" placeholder="Password" onChange={handleChange} required/>
          <button type="submit">Register</button>
        </form>
        <p className="auth-link">Already have an account? <a href="/login">Login</a></p>
      </div>
    </div>
  );
};

export default Register;
