import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import { useAuth } from "../context/AuthContext";
import "./Auth.css";

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();

  const API_URL = "http://localhost:5000/api/posts";

  // Fetch all posts
  const fetchPosts = async () => {
    setLoading(true);
    try {
      const res = await axios.get(API_URL);
      setPosts(res.data);
    } catch (err) {
      console.error(err);
      alert("Error fetching posts");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  // Delete a post
  const handleDelete = async (postId) => {
    if (!window.confirm("Are you sure you want to delete this post?")) return;
    try {
      await axios.delete(`${API_URL}/${postId}`);
      setPosts(posts.filter((p) => p._id !== postId)); // remove from state instantly
    } catch (err) {
      console.error(err);
      alert("Error deleting post");
    }
  };

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-main">
        {loading && (
          <div className="spinner-container">
            <div className="spinner"></div>
          </div>
        )}

        <div className="post-feed">
          {!loading && posts.length === 0 && <p>No posts to show.</p>}

          {posts.map((post) => (
            <div className="post-card" key={post._id}>
              <div className="post-header">
                <img
                  className="post-avatar"
                  src={
                    post.user?.avatar
                      ? post.user.avatar.startsWith("http")
                        ? post.user.avatar
                        : `/uploads/${post.user.avatar}`
                      : "/default-avatar.png"
                  }
                  alt="avatar"
                />
                <div>
                  <span className="post-username">{post.user?.username || "Unknown"}</span>
                  <br />
                  <span className="post-time">
                    {new Date(post.createdAt).toLocaleString()}
                  </span>
                </div>
              </div>

              <div className="post-content">
                <p className="post-caption">{post.caption || post.content}</p>
                {post.image && (
                  <img
                    className="post-image"
                    src={post.image.startsWith("http") ? post.image : `/uploads/${post.image}`}
                    alt="post"
                  />
                )}
              </div>

              {user && user._id === post.user?._id && (
                <button
                  className="delete-btn"
                  onClick={() => handleDelete(post._id)}
                >
                  Delete
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
