import React from "react";
import "./Auth.css";

const PostCard = ({ post, userId, onDelete }) => {
  const IMAGE_BASE_URL = "http://localhost:5000"; // adjust to your backend URL

  return (
    <div className="post-card">
      <div className="post-header">
        <img
          className="post-avatar"
          src={post.userAvatar || "/default-avatar.png"}
          alt="avatar"
        />
        <div>
          <span className="post-username">{post.username}</span>
          <br />
          <span className="post-time">
            {new Date(post.createdAt).toLocaleString()}
          </span>
        </div>
      </div>

      <div className="post-content">
        <p>{post.content}</p>
        {post.image && (
          <img
            className="post-image"
            src={post.image.startsWith("http") ? post.image : `${IMAGE_BASE_URL}${post.image}`}
            alt="post"
          />
        )}
      </div>

      {userId === post.user && (
        <button className="delete-btn" onClick={() => onDelete(post._id)}>
          Delete
        </button>
      )}
    </div>
  );
};

export default PostCard;
