import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import PostList from "./PostList";
import Avatar from "./Avatar";
import "./Auth.css";

const Profile = () => {
  const { user, logout } = useAuth();
  const { userId } = useParams();
  const [posts, setPosts] = useState([]);
  const [profileUser, setProfileUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user info
    axios
      .get(`http://localhost:5000/api/users/${userId}`)
      .then((res) => setProfileUser(res.data))
      .catch((err) => console.error(err));

    // Fetch posts
    axios
      .get(`http://localhost:5000/api/posts/user/${userId}`)
      .then((res) => setPosts(res.data))
      .catch((err) => console.error(err));
  }, [userId]);

  const handleDeleteProfile = async () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      try {
        await axios.delete(`http://localhost:5000/api/users/${user.id}`);
        logout();
        navigate("/login");
      } catch (err) {
        console.error(err);
        alert("Failed to delete profile");
      }
    }
  };

  if (!profileUser) return <p>Loading...</p>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img
          src={`http://localhost:5000/uploads/${profileUser.avatar}`}
          alt="Avatar"
          className="profile-avatar-big"
        />
        <h2>{profileUser.username}</h2>

        {user.id === profileUser._id && (
          <div className="profile-buttons">
            <Avatar /> {/* change avatar */}
            <button onClick={handleDeleteProfile} className="delete-btn">
              Delete Profile
            </button>
          </div>
        )}
      </div>

      <PostList posts={posts} />
    </div>
  );
};

export default Profile;
