import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Navbar from "./Navbar";
import "./Auth.css";

const Profile = () => {
  const { id: paramId } = useParams();
  const { user } = useAuth();
  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user && !paramId) {
      setError("User not logged in.");
      setLoading(false);
      return;
    }

    const userId = paramId || user?._id;

    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setProfile(res.data);
        setPosts(res.data.posts || []);
      } catch (err) {
        console.error("Error fetching profile:", err);
        setError("Failed to fetch profile.");
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [user, paramId]);

  const handleAvatarChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("avatar", file);

    try {
      await axios.put(
        `http://localhost:5000/api/users/${user._id}/avatar`,
        formData
      );
      window.location.reload();
    } catch (err) {
      console.error("Error updating avatar:", err);
    }
  };

  if (loading) return <p style={{ marginTop: "50px" }}>Loading profile...</p>;
  if (error) return <p style={{ marginTop: "50px", color: "red" }}>{error}</p>;

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-main">
        <div className="profile-header" style={{ flexDirection: "column" }}>
          <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
            <img
              src={profile.avatar || "/default-avatar.png"}
              alt="avatar"
              className="profile-avatar-big"
            />
            <div>
              <h2>{profile.username}</h2>
              <div style={{ display: "flex", gap: "15px", marginTop: "5px" }}>
                <span><strong>{posts.length}</strong> posts</span>
                <span><strong>{profile.followers?.length || 0}</strong> followers</span>
                <span><strong>{profile.following?.length || 0}</strong> following</span>
              </div>
            </div>
          </div>

          {/* Avatar upload button for logged-in user */}
          {user && user._id === profile._id && (
            <div className="avatar-upload-container">
              <label className="avatar-upload-label">
                Change Avatar
                <input type="file" onChange={handleAvatarChange} />
              </label>
            </div>
          )}
        </div>

        <div className="post-feed" style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))", gap: "15px" }}>
          {posts.length > 0 ? (
            posts.map((post) => (
              <div key={post._id} className="post-card">
                {post.image && <img src={post.image} alt="Post" className="post-image" />}
                <p>{post.caption}</p>
              </div>
            ))
          ) : (
            <p>No posts yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
