import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./Profile.css";

function Profile() {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const userRes = await axios.get(`http://localhost:5000/api/users/${userId}`);
        const postsRes = await axios.get(`http://localhost:5000/api/posts/user/${userId}`);
        setUser(userRes.data);
        setPosts(postsRes.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProfileData();
  }, [userId]);

  if (loading) return <p>Loading...</p>;

  if (!user) return <p>User not found.</p>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img src={user.avatar || "/default-avatar.png"} alt="avatar" className="profile-avatar" />
        <h2>{user.username}</h2>
      </div>

      <div className="profile-posts">
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id} className="profile-post-card">
              {post.image && <img src={post.image} alt="Post" className="profile-post-image" />}
              <p className="profile-post-caption">{post.caption}</p>
            </div>
          ))
        ) : (
          <p>No posts yet.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
