import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "./Auth.css";

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div className="navbar">
      <h2 className="nav-title">Connectly</h2>
      <ul className="nav-links">
        <li><Link to="/dashboard">üè† Home</Link></li>
        <li><Link to="/search">üîç Search</Link></li>
        <li><Link to="/explore">üåê Explore</Link></li>
        <li><Link to="/reels">üé• Reels</Link></li>
        <li><Link to="/messages">üí¨ Messages</Link></li>
        <li><Link to="/notifications">üîî Notifications</Link></li>
        <li><Link to="/posts/new">‚ûï Create</Link></li>
        <li><Link to={`/profile/${user?._id}`}>üë§ Profile</Link></li>
        <li><button onClick={handleLogout}>üö™ Logout</button></li>
      </ul>
    </div>
  );
};

export default Navbar;
