import React from "react";
import "./Auth.css";

const PostCard = ({ post, onDelete }) => {
  return (
    <div className="post-card">
      <div className="post-header">
        <img
          className="post-avatar"
          src={post.user?.avatar || "/default-avatar.png"}
          alt="avatar"
        />
        <div>
          <strong className="post-username">{post.user?.username || "Unknown"}</strong>
          <div className="post-time">{new Date(post.createdAt).toLocaleString()}</div>
        </div>
      </div>

      {post.image && (
        <img
          className="post-image"
          src={post.image.startsWith("http") ? post.image : `/uploads/${post.image}`}
          alt="post"
        />
      )}

      <p className="post-caption">{post.caption}</p>

      {/* Delete Button */}
      <button className="delete-btn" onClick={() => onDelete(post._id)}>
        Delete
      </button>
    </div>
  );
};

export default PostCard;
