import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import { Link, useNavigate } from "react-router-dom";
import "./Auth.css";

function Dashboard() {
  const { user, logout } = useAuth();
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Redirect if not logged in
  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user, navigate]);

  // Fetch posts once user is available
  useEffect(() => {
    if (!user?._id) return;

    const fetchPosts = async () => {
      setLoading(true);
      setError("");
      try {
        const res = await axios.get("http://localhost:5000/api/posts", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setPosts(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load posts");
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, [user?._id]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  if (!user || loading) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px" }}>
        <h2>{loading ? "Loading posts..." : "Please login to access the dashboard"}</h2>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <Link to={`/profile/${user._id}`} className="dashboard-user">
          <img
            src={user.avatar || "/default-avatar.png"}
            alt="avatar"
            className="dashboard-avatar"
          />
          <span className="dashboard-username">{user.username}</span>
        </Link>
        <button onClick={handleLogout} style={{ marginLeft: "20px" }}>
          Logout
        </button>
      </div>

      {/* Create Post Button */}
      <div style={{ margin: "20px 0", textAlign: "center" }}>
        <Link to="/posts/new">
          <button>Create New Post</button>
        </Link>
      </div>

      {/* Posts Feed */}
      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
      <div className="post-feed">
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id} className="post-card">
              <div className="post-header">
                <img
                  src={post.user?.avatar || "/default-avatar.png"}
                  alt="avatar"
                  className="post-avatar"
                />
                <span className="post-username">{post.user?.username}</span>
              </div>
              {post.image && <img src={post.image} alt="Post" className="post-image" />}
              <p className="post-caption">{post.caption}</p>
            </div>
          ))
        ) : (
          <p style={{ textAlign: "center" }}>No posts yet.</p>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
