import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import axios from "axios";
import PostList from "./PostList";
import { useNavigate } from "react-router-dom";
import Avatar from "./Avatar"; // avatar upload component
import "./Auth.css";

const Dashboard = () => {
  const { user, logout } = useAuth();
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user) return;
    axios
      .get(`http://localhost:5000/api/posts/user/${user.id}`)
      .then((res) => setPosts(res.data))
      .catch((err) => console.error(err));
  }, [user]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const handleAvatarClick = () => {
    navigate("/profile");
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-topbar">
        <div className="dashboard-user-info" onClick={handleAvatarClick} style={{ cursor: "pointer" }}>
          <img
            src={`http://localhost:5000/uploads/${user.avatar}`}
            alt="Avatar"
            className="post-avatar"
          />
          <span className="dashboard-username">{user.username}</span>
        </div>
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <PostList posts={posts} />
    </div>
  );
};

export default Dashboard;
