import React from "react";
import "./Auth.css";

const PostCard = ({ post }) => {
  const imageUrl = post.image ? `http://localhost:5000/uploads/${post.image}` : null;

  return (
    <div className="post-card">
      <div className="post-header">
        <img
          src={post.user.avatar ? `http://localhost:5000/uploads/${post.user.avatar}` : "https://via.placeholder.com/40"}
          alt="avatar"
          className="post-avatar"
        />
        <span className="post-username">{post.user.username}</span>
      </div>
      {imageUrl && <img src={imageUrl} alt="post" className="post-image" />}
      <div className="post-caption">{post.title}</div>
    </div>
  );
};

export default PostCard;
