import React, { useState } from 'react';
import axios from 'axios';

const PostForm = () => {
  const [caption, setCaption] = useState('');
  const [media, setMedia] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('caption', caption);
    if (media) formData.append('media', media);
    try {
      await axios.post('http://localhost:5000/api/posts', formData);
      setCaption('');
      setMedia(null);
      window.location.reload(); // Simple refresh; use state management for better UX
    } catch (err) {
      alert('Post failed');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <textarea placeholder="Caption" value={caption} onChange={(e) => setCaption(e.target.value)} />
      <input type="file" accept="image/*,video/*" onChange={(e) => setMedia(e.target.files[0])} />
      <button type="submit">Post</button>
    </form>
  );
};

export default PostForm;
