import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Navbar from "./Navbar";
import "./Auth.css";

const Profile = () => {
  const { id: paramId } = useParams();
  const { user } = useAuth();
  const [userId, setUserId] = useState(paramId || user?._id);
  const [profile, setProfile] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Update userId if user context loads later
  useEffect(() => {
    if (!paramId && user?._id) {
      setUserId(user._id);
    }
  }, [paramId, user]);

  useEffect(() => {
    if (!userId) return; // Stop if no userId

    const fetchProfile = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
        setProfile(res.data);
        setPosts(res.data.posts || []);
      } catch (err) {
        console.error("Error fetching profile:", err);
        setProfile(null);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [userId]);

  const handleAvatarChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append("avatar", file);

    try {
      await axios.put(`http://localhost:5000/api/users/${userId}/avatar`, formData);
      // Reload profile without refreshing page
      const res = await axios.get(`http://localhost:5000/api/users/${userId}`);
      setProfile(res.data);
    } catch (err) {
      console.error("Error updating avatar:", err);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-layout">
        <Navbar />
        <div className="dashboard-main">
          <div className="spinner-container">
            <div className="spinner"></div>
          </div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="dashboard-layout">
        <Navbar />
        <div className="dashboard-main">
          <p>User not found.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-main">
        <div className="profile-header">
          <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
            <img
              src={profile.avatar || "/default-avatar.png"}
              alt="avatar"
              className="profile-avatar-big"
            />
            <div>
              <h2>{profile.username}</h2>
              <div style={{ display: "flex", gap: "15px", fontSize: "14px", color: "#555" }}>
                <span>{posts.length} posts</span>
                <span>{profile.followers?.length || 0} followers</span>
                <span>{profile.following?.length || 0} following</span>
              </div>
              {user && user._id === userId && (
                <div className="avatar-upload-container">
                  <label className="avatar-upload-label">
                    Change Avatar
                    <input type="file" onChange={handleAvatarChange} />
                  </label>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="post-feed">
          {posts.length > 0 ? (
            posts.map((post) => (
              <div key={post._id} className="post-card">
                {post.image && <img src={post.image} alt="Post" className="post-image" />}
                <p>{post.caption}</p>
              </div>
            ))
          ) : (
            <p>No posts yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
