// Dashboard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./Navbar"; // your existing navbar
import { useAuth } from "../context/AuthContext"; // get current user
import "./Auth.css";

const Dashboard = () => {
  const [posts, setPosts] = useState([]);
  const { user } = useAuth(); // logged-in user

  const API_URL = "http://localhost:5000/api/posts"; // update port if different

  // Fetch all posts
  const fetchPosts = async () => {
    try {
      const res = await axios.get(API_URL);
      setPosts(res.data);
    } catch (err) {
      console.error(err);
      alert("Error fetching posts");
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  // Delete a post
  const handleDelete = async (postId) => {
    if (!window.confirm("Are you sure you want to delete this post?")) return;
    try {
      await axios.delete(`${API_URL}/${postId}`);
      fetchPosts(); // refresh feed
    } catch (err) {
      console.error(err);
      alert("Error deleting post");
    }
  };

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-main">
        <div className="post-feed">
          {posts.length === 0 && <p>No posts to show.</p>}
          {posts.map((post) => (
            <div className="post-card" key={post._id}>
              <div className="post-header">
                <img
                  className="post-avatar"
                  src={post.userAvatar || "/default-avatar.png"}
                  alt="avatar"
                />
                <span className="post-username">{post.username}</span>
              </div>
              <p>{post.content}</p>
              {post.image && (
                <img className="post-image" src={post.image} alt="post" />
              )}
              {/* Show delete button only for posts owned by the logged-in user */}
              {user && user._id === post.user && (
                <button
                  className="delete-btn"
                  onClick={() => handleDelete(post._id)}
                >
                  Delete
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
