import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import "./Auth.css";
import logo from "../assets/logo.png"; // ‚úÖ same logo

const Login = () => {
  const { loginUser } = useAuth();
  const [formData, setFormData] = useState({
    email: "",
    password: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    loginUser(formData);
  };

  return (
    <div className="auth-page">
      <div className="auth-header">
        <img src={logo} alt="App Logo" className="auth-logo" />
        <h1 className="auth-appname">Connectly</h1>
        <p className="auth-slogan">Share your world with friends üåç</p>
      </div>

      <div className="auth-container">
        <h2>Welcome Back</h2>
        <p className="auth-tagline">Log in to continue sharing your world</p>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleChange}
            required
          />
          <button type="submit">Login</button>
        </form>
        <p className="auth-link">
          New here? <a href="/register">Create Account</a>
        </p>
      </div>
    </div>
  );
};

export default Login;
